import styled from "styled-components";
import { Link } from "react-router-dom";

export const FotterContainer = styled.fotter`
  color: #101522;
`;

export const FotterWrap = styled.view`
  padding: 48px 24px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  max-width: 1100px;
  margin: 0 auto;
`;

export const FotterLinksContainer = styled.div`
  display: flex;
  justify-content: center;

  @media screen and (max-width: 768px) {
    padding: 32px;
  }
`;
export const FotterLinksWrapper = styled.div`
  display: flex;
  @media screen and (max-width: 768px) {
    flex-direction: column;
  }
`;

export const FotterLinkItems = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin: 16px;
  text-align: left;
  width: 160px;
  box-sizing: border-box;
  color: #fff;

  @media screen and (max-width) {
    margin: 0;
    padding: 10px;
    width: 100%;
  }
`;

export const FotterLinkTitle = styled.h1`
  font-size: 14px;
  margin-bottom: 16px;
`;

export const FotterLink = styled(Link)`
  font-size: 14px;
  color: #fff;
  text-decoration: none;
  margin-bottom: 0.5rem;
  &:hover {
    color: #01bf71;
    transition: 0.3s ease-out;
  }
`;
